----------------------------------------------
-- TF-IDF를 위한 테이블. (용어를 저장.)
----------------------------------------------

-------------------------------------------------------
-- 분석 모드를 선택가능
-------------------------------------------------------
CREATE TABLE tf_mode
(
	 id				INT				IDENTITY(1,1)	not null
	,[token_type]	NVARCHAR(64)					not null
	,[stem_type]	NVARCHAR(64)					not null
	,[description]  NVARCHAR(512)
	primary key(id)
);;


-------------------------------------------------------
-- 불용어 저장 테이블 (분석 인스턴스 마다 한 개)
-------------------------------------------------------
CREATE TABLE stopword
(
	id			INTEGER			IDENTITY(1,1)	not null
	,mode_id	INTEGER							NOT NULL
	,name		NVARCHAR(512)					not null
	primary key(id)
);;
CREATE NONCLUSTERED INDEX stopword_idx_mode_id	ON stopword(mode_id);;
CREATE NONCLUSTERED INDEX stopword_idx_nameON	ON stopword(name);;


-------------------------------------------------------
-- 모든 용어들을 저장하는 테이블
-------------------------------------------------------
CREATE TABLE term
(
	 mode_id	int							not null
	,id			int							not null	
	,value		NVARCHAR(512)				not null
	primary key(mode_id, id)
);;


-------------------------------------------------------
-- 모든 요소들의 용어를 저장한 테이블
-------------------------------------------------------
CREATE TABLE element_term
(
	 mode_id	int			not null			--모드 아이디
	,site_id	INTEGER 	not null			--사이트 아이디
	,src_type	NVARCHAR(32) not null			--소스의 타입
	,src_id		int			not null			--소스의 아이디
	,seq		int			not null			--용어의 순서
	,term_id	int			not null			--용어 아이디
	,stopword	BIT			not null			--불용어인지에 대한 확인 필드
	,project_id	int			not null			--프로젝트 아이디 (분석용)
	,bug_id		int			not null			--버그 아이디  (분석용)
	primary key(mode_id, site_id, src_type, src_id, seq)
);;
CREATE NONCLUSTERED INDEX element_term_project_bug_id	ON element_term(project_id, bug_id);;
CREATE NONCLUSTERED INDEX element_term_term_id			ON element_term(term_id);;
CREATE NONCLUSTERED INDEX element_term_mode_bug_id		ON element_term(mode_id, site_id, bug_id, src_type);;


