CREATE PROCEDURE addMode
(
	 @id				Integer = Null
	,@token_type		NVARCHAR(32)
	,@stem_type			NVARCHAR(32)
	,@description		NVARCHAR(512) = ''
)
AS
BEGIN
	IF @id is not null
	BEGIN	
		IF EXISTS (SELECT id from tf_mode Where id = @id and token_type = @token_type and stem_type = @stem_type)
		begin
			SELECT @id as ID
			RETURN @id
		end
	end
	

	INSERT INTO tf_mode VALUES (@token_type, @stem_type, @description)
	SELECT @@IDENTITY as ID
	RETURN @@IDENTITY
END;;


-------------------------------------------
--불용어를 등록한다.
-------------------------------------------
CREATE PROCEDURE addStopwords
(
	 @mode_id			integer
	,@xml				XML
)
AS
BEGIN
	BEGIN TRY
		--XML로 부터 테이블 생성.
		INSERT INTO stopword
		SELECT
			 @mode_id
			,b.value(N'@value',		N'varchar(128)')	as value
		FROM @xml.nodes(N'/List/E')			as a(b) 
	END TRY
	BEGIN CATCH
		DECLARE @DataError NVARCHAR(4000)
		SET @DataError = N'Error' + CONVERT(NVARCHAR(10), ERROR_NUMBER()) + N':' + ERROR_MESSAGE()
		PRINT @DataError
		RETURN -1	--RAISERROR (@dataError, 16,1)
	END CATCH
	RETURN 1
END;;


-------------------------------------------
--분석된 문장들을 입력한다.
--입력 형식 : 
-- <Es>
--		<E src_type="" src_id="" seq="" term="" stopwords="" />
--		<E src_type="" src_id="" seq="" term="" stopwords="" />
--		....
-- </Es>
-------------------------------------------
CREATE PROCEDURE addElements
(
	 @mode_id			int
	,@site_id			int
	,@project_id		int
	,@bug_id			int
	,@src_type			NVARCHAR(10)
	,@src_id			int
	,@xml				XML
)
AS
BEGIN
	--SET NOCOUNT ON

	DECLARE @element AS TABLE
	(
		 seq		int				not null
		,term		varchar(256)	not null
		primary key(seq)
	)
	


	BEGIN TRY
		--XML로 부터 테이블 생성.
		INSERT @element
		SELECT 
			 b.value(N'@seq',		N'int')				as seq
			,b.value(N'@term',		N'varchar(256)')	as term
		FROM @xml.nodes(N'/List/E')			as a(b) 

		
		--term에 입력할 데이터의 시작 id를 구함
		DECLARE @start_id INTEGER 
		SELECT @start_id = max(id) FROM term WHERE mode_id = @mode_id;
		if(@start_id is null) SET @start_id = 0;

		
		--없는 단어들 term 테이블에 입력
		WITH NEW_TERMS(value) AS
		(
			SELECT DISTINCT 
				e.term as value
			FROM @element e 
			left join term t on t.mode_id = @mode_id and t.value = e.term 
			where t.value is null
		)
		INSERT INTO term
		SELECT
			 @mode_id										as mode_id
			,@start_id + ROW_NUMBER() over(ORDER BY value)	as id
			,value											as value	
		FROM NEW_TERMS



		--추가된단어들 모두 테이블에 입력.
		INSERT INTO element_term
		SELECT @mode_id, @site_id, @src_type, @src_id, e.seq, t.id, 0, @project_id, @bug_id 
		FROM @element e 
		join term t on t.value = e.term and t.mode_id = @mode_id

	END TRY
	BEGIN CATCH
		DECLARE @DataError NVARCHAR(4000)
		SET @DataError = N'Error' + CONVERT(NVARCHAR(10), ERROR_NUMBER()) + N':' + ERROR_MESSAGE()
		PRINT @DataError
		RETURN -1	--RAISERROR (@dataError, 16,1)
	END CATCH
	RETURN 1
END;;


-------------------------------------------
-- 항목별 TF확인 뷰.
-------------------------------------------
CREATE VIEW element_tf
AS
SELECT 
	 e.mode_id
	,e.site_id
	,e.project_id
	,e.bug_id
	,e.src_type
	,e.src_id
	,e.stopword
	,t.value		AS term
	,count(t.value)	AS frequency 
FROM element_term e
join term t on e.mode_id = t.mode_id and e.term_id = t.id
GROUP BY e.mode_id, e.site_id, e.project_id, e.bug_id, e.src_type, e.src_id, e.stopword, t.value
;;


-------------------------------------------
-- 문서별 TF확인 뷰
-------------------------------------------
CREATE VIEW bug_tf
AS
SELECT 
	 a.mode_id
	,a.site_id
	,a.project_id
	,a.bug_id
	,a.stopword
	,t.value		AS Term
	,a.frequency
FROM
	(SELECT 
		 e.mode_id
		,e.site_id
		,e.project_id
		,e.bug_id
		,e.stopword
		,e.term_id
		,count(e.term_id)	AS frequency 
	FROM element_term e
	GROUP BY e.mode_id, e.site_id, e.project_id, e.bug_id, e.stopword, e.term_id) a
JOIN term t ON a.mode_id = t.mode_id and a.term_id = t.id
;;

-------------------------------------------
-- 프로젝트별 TF확인 뷰
-------------------------------------------
CREATE VIEW project_tf
AS
SELECT 
	 e.mode_id
	,site_id
	,e.project_id
	,e.stopword
	,t.value		AS Term
	,count(t.value)	AS frequency 
FROM element_term e
join term t on e.mode_id = t.mode_id and e.term_id = t.id
GROUP BY e.mode_id, e.site_id, e.project_id, e.stopword, t.value
;;


-------------------------------------------
-- 모드 내 전체 용어에 대한 TF확인
-------------------------------------------
CREATE VIEW site_tf
AS
SELECT 
	 e.mode_id
	,e.site_id
	,e.stopword
	,t.value		AS Term
	,count(t.value)	AS frequency 
FROM element_term e
join term t on e.mode_id = t.mode_id and e.term_id = t.id
GROUP BY e.mode_id, e.site_id, e.stopword, t.value
;;


-------------------------------------------
-- 모드 내 전체 용어에 대한 TF확인
-------------------------------------------
CREATE VIEW term_tf
AS
SELECT 
	 e.mode_id
	,e.stopword
	,t.value		AS Term
	,count(t.value)	AS frequency 
FROM element_term e
join term t on e.mode_id = t.mode_id and e.term_id = t.id
GROUP BY e.mode_id, e.stopword, t.value
;;




-------------------------------------------------------
-- stopword를 보충
-------------------------------------------------------
CREATE PROCEDURE makeStopword
(
	 @siteID	as Integer
	,@modeID	as Integer
	,@initSize integer	= null	--초기의 stopword size
)
AS
BEGIN
	IF @initSize is null SET @initSize = 0

	--stopword초기화
	DECLARE @minID as integer
	SELECT @minID = min(id) + @initSize from stopword where mode_id =@modeID
	DELETE FROM stopword where mode_id =@modeID and id>=@minID


	--상한선 하한선 을 구함.
	DECLARE @lLimit as DECIMAL(10,5) = 3.0
	DECLARE @rLimit as DECIMAL(10,5)
	SELECT @rLimit = (max(value) / 10.0) FROM (
		--IDF의 분포를 보여줌.
		SELECT
			 DISTINCT value
		from (SELECT term_id, cast((value*10) as integer) as value FROM getIDF(@modeID, @siteID) ) a
		group by value
	) t

	INSERT INTO stopword
	SELECT @modeID, value from (
		--stopword로 포함시킬 어휘들.
		SELECT value FROM TERM T
		WHERE mode_id = @modeID and
		id in (SELECT term_id FROM getIDF(@modeID,@siteID) where value < @lLimit or value >= @rLimit)
		union
		SELECT value FROM TERM 
		WHERE MODE_ID = @modeID					--작업하는 모드
		and ( len(value) <=@modeID 
			OR VALUE like '[0-9]%' 
			OR charindex('_', value) > 0)			--포함시키고 싶은 단어들
		and value not in ('qa', 'ui','ip')			--예외로 주고싶은 단어들
		union SELECT '...'
		union select value from term where value like '[_][_]%' and mode_id = @modeID
	)t
	WHERE value not in (SELECT name FROM stopword WHERE mode_id = @modeID)
END;;

-------------------------------------------------------
-- stopword에 해당하는 값을 표시
-------------------------------------------------------
CREATE PROCEDURE setStopwords
(
	 @siteID	as Integer
	,@modeID	as Integer
)
AS
BEGIN

	IF @siteID is null and @modeID <0 RETURN -1

	--모든 값을 초기화
	UPDATE	et
	SET		stopword = 0
	FROM	element_term et 
	WHERE	mode_id = @modeID


	--Stopword가 맞는 값에 대해서 1로 적용
	UPDATE	et
	SET		et.stopword = 1
	--SELECT mode_id, site_id, term_id, stopword 
	FROM	element_term et 
	WHERE	site_id = @siteID and mode_id = @modeID
	AND		term_id in (
		--불용어인 term_id
		SELECT	 t.id
		FROM	term t
		WHERE	t.mode_id = @modeID
		AND		t.value in (SELECT name FROM stopword WHERE mode_id = @modeID)
	)
END;;