	/**
	 * 파일에 있는 리스트에 해당하는 버그아이디들을 출력 패스로 출력.
	 * @param _listfile
	 * @param _depth
	 * @param _size
	 * @param _outputPath
	 * @return
	 */
	public boolean makeXMLwithCount(String _listfile, int _depth, int _size, String _outputPath)//Map<Integer,Integer> _map)
	{
		//작업대상 파일 로드
		Map<Integer, Integer> list = setHash(_listfile);
		if(list==null) return false;
		
		//각 폴더내 값에 대한 인덱스 생성.
		int[] index = getIndexWithOne(1000);
		
		//기타 변수 기본값 설정.
		String src=null;
		File outDir = null;
		File input = null;
		
	
		//타겟 디렉토리 생성
		outDir = new File(_outputPath);
		if(outDir.exists()==false)
		{
			if(outDir.mkdirs()==false)
			{
				System.out.println("Output Create Error");
				return false;
			}
		}
		
		long k=0;
		long Max_Size = list.size();
		
		//리스트 순회(map list) : Map = ID	count    ===>   count별로 폴더 구분.
		Iterator<Integer> keys = list.keySet().iterator();
        while(keys.hasNext()){
        
        	//키 추출
            Integer key = keys.next();
            Integer count = list.get(key);
            
            //소스파일의 실제 경로 추출.
            src = TextUtil.convertURLtoPath(bugPage.replaceFirst("\\{0\\}", Integer.toString(key)));
            src = TextUtil.makeBucket(src, _size, _depth);
            src = LocalProxyPath+ src;
			//System.out.println(path);

			//파일 존재 확인
			input = new File(src);
			if(input.exists()==false)
			{
				log.error("["+k+"/"+Max_Size+"] No file.("+key+"): "+ src);
				continue;
			}
			
			//파일 복사.(카운트별 폴더로 넣음)
			String out = OutputPath +count+"\\"+ index[count] +".xml";	//복사대상
			createPath(out);											//부모 디렉토리 생성
			fileCopy(src, out);											//파일복사
			
			
			//결과출력
			if(k%100==0) log.info("["+k+"/"+Max_Size+"] Success.("+key+"): "+ src);	//결과 출력.(100개에 한번..)

			//인덱스 정리.
			index[count]++;
			k++;            
        }
        
        
        //최종결과 출력
        if(k==Max_Size){
        	log.info("["+k+"/"+Max_Size+"] Success all works.");
        	return true;
        }
        else{
        	log.error("["+k+"/"+Max_Size+"] Done with some Errors.");
        	return false;
        }
	}
	
	


	/**
	 * 파일에 있는 리스트에 해당하는 버그아이디들을 출력 패스로 출력.
	 * 
	 */
	public void makeXML(String[] idList, String _outputPath)
	{
		String path=null;
		File file = null;
		
		//타겟 디렉토리 생성
		file = new File(_outputPath);
		if(file.exists()==false)
		{
			if(file.mkdirs()==false)
			{
				System.out.println("Create Error");
				return;
			}
		}
		
		
		//리스트 순회
		for(int i=0; i<idList.length; i++)
		{
			path = TextUtil.convertURLtoPath(bugPage.replaceFirst("\\{0\\}", idList[i]));
			path = LocalProxyPath+ path;

			//파일 존재 확인 및 복사
			file = new File(path);
			if(file.exists()==true)
			{
				fileCopy(path, _outputPath + i +".xml");
				log.info("["+(i+1)+"/"+idList.length+"] Success.("+idList[i]+"): "+ path);
			}
			else{
				log.error("["+(i+1)+"/"+idList.length+"] No file.("+idList[i]+"): "+ path);
			}

		}

	}


	
